package firstprogram;

public class PrintOddnEvenNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int i=0;
        for( i=1;i<=20;i++) {
        	if(i%2==0)
        	{
        		System.out.println("i="+ i +"is a even number");
        	}
        	else
        	{
        		System.out.println("i="+ i +"is a odd number");
        	}
        		
        }
	}

}
