package firstprogram;

public class ToPrintEvenNumberBetween10And100UsingWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int i=10;
        while(i<=100)
        {
        	if(i%10==0)
        	
        		System.out.println("i="+i+"is a even number");
        		i++;
        	
        }
        
}
}
